import React, { useState, Fragment } from 'react';
import api from '../api';
import PicComponent from './PicComponent';

const ForgetPass = () => {

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        password2: ''
    });

    const {email, password, password2} = formData;

    //Password Validation
    const isContainNumber = /^(?=.*[A-Z]).*$/;
    const isContainSymbol = /^(?=.*[~`!@#$%^&*()--+={}[\]|:;"'<>,.?/_₹]).*$/;


    const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value});

    const onSubmit = async (e) => {
        e.preventDefault();

        if(password!==password2)
            return alert("Password Do not Match!");
        else if(password.length < 10){
            return alert('Password should be longer than 10 characters');
        } 
        else if(!isContainNumber.test(password)){
            return alert('Password should container atleast one Upper case character')
        }
        else if(!isContainSymbol.test(password)){
            return alert('Password should container atleast one special character');
        }
        else{
            console.log("Password seems fine");

            const newUser = {
                email,
                password
            };

            try
            {
                const config = {
                    header:{
                        'Content-Type': 'application/json'
                    }
                };

                const body = JSON.stringify(newUser)
                const res = await api.put('/users', body, config);
                console.log(res.data);

            }
            catch(err){
                console.log(err.response.data);
            }
        }



    }

    return (
    <Fragment>
        <div className='row'>
            <div className='col-8'>
                <PicComponent />
            </div>
            <div className='App-header col-4'>
                <h3 className='green'> Forgot Password?</h3>
                <form className='form mt-4 p-2' onSubmit={e => onSubmit(e)}>
                <div className='form-group'>
                    <label> Email</label>
                    <input
                        className='form-control' 
                        type="text"
                        value={email}
                        name="email"
                        placeholder='eg. JohnDoe@gmail.com'
                        onChange={e => onChange(e)}
                    />
                </div>
                <span className='f6'> Password should contain atleast one Uppercase character and one Special character</span>
                <div className='form-group'>
                    <label> New Password</label>
                    <input 
                        className='form-control'
                        type="password"
                        value={password1}
                        name="password1"
                        placeholder='Password'
                        onChange={e => onChange(e)}
                    />
                </div>
                <div className='form-group'>
                    <label> Re-type Password</label>
                    <input 
                        className='form-control'
                        type="password"
                        value={password2}
                        name="password2"
                        placeholder='Re-type Password'
                        onChange={e => onChange(e)}
                    />
                </div>
                <button className='btn btn-success mt-2' type='submit' name='ForgetPass'> Submit </button>
            </form>
            </div>
        </div>
    </Fragment>
  )
}

export default ForgetPass;
