import React, { Fragment, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../api';

const LoginForm = () => {

    let message;

     const [formData, setFormData ] = useState({
        email: '',
        password: ''
     });

     const [flag, setFlag] = useState({
        flag: false
     });

     localStorage.clear();
     let navigate = useNavigate(); 

     const {email, password} = formData

     const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

     const onSubmit = async (e) => {
        e.preventDefault();
        console.log(formData);

        try 
        {
            const res = await api.post('/auth', formData);

            if(res.data.errors)
            {
                setFlag(true);
                message = res.data.errors
                console.log(res.data.errors);
            }
            else
            {
                localStorage.token = res.data.token;
                setFlag(false);  
                navigate("/Landing");
            }
        }
        catch(err)
        {
                message = err.data;
                console.log(message)
        }

     }
    return (
    <Fragment>
    <form className='form' onSubmit={e => onSubmit(e)}>
      <div className='container p-2 App-header'>
        <div className='d-flex align-items-stretch'>
            <h2> <b> Log in to <span className='green'> eleven</span> </b> </h2>
        </div>
        <div className='d-flex align-items-stretch p-2'>
            { 
                flag ? ''
                     : <div>{message}</div> 
            }
        </div>
        <div className='d-flex align-self p-2' >
            <span> Email </span>
            <input 
                type="text" 
                name='email'
                value = {email}
                placeholder='Email'
                onChange={(e)=> onChange(e)} 
            />
        </div>
        <div className='d-flex p-2 align-self' >
            <span> Password </span>
            <input 
                type="password" 
                name='password'
                value={password}
                minLength={6}
                placeholder='Password'
                onChange={(e)=> onChange(e)} 
            />
        </div>

        </div>
        <div className='p-2'>
            <span> 
                <a className='green' href='/forget-password' > Forget password </a> 
            </span>
        </div>
        <div className="row mt-2">
            <div className="container">
                <input className="btn btn-success" type='submit' name="Login" />
            </div>
        </div>
        <div className='mt-2'>
            <span> <Link to='/Register' className='green'> User does not exist? Register here  </Link> </span>
        </div>
    </form>
    </Fragment>
  )
}

export default LoginForm;
